(function(exports){
    var measured  = require('measured');

    exports.instruments = {
        requests      : new measured.Counter(),     //requests_sent
        reads         : new measured.Counter(),     //requests_sent
        writes        : new measured.Counter(),     //requests_sent
        responses     : new measured.Counter(),     //total successful responses
        bytes_sent    : new measured.Counter(),     //bytes_sent
        bytes_received: new measured.Counter(),     //bytes_received
        errors_total  : new measured.Counter(),     //total errors
        stalls        : new measured.Counter(),     //throttled requests
        resets        : new measured.Counter(),     //server quit
        timeouts      : new measured.Counter()      //client quit
    };

})(module.exports);